pragma solidity ^0.5.0;

import "openzeppelin-solidity/contracts/ownership/Ownable.sol";
import "./BNDESRegistry.sol";


contract BNDESResolver is Ownable() {
    
    address bndesRegistryAddr;
    address[] previousbndesRegistryAddr;

    function changeBNDESRegistryContract(address newAddr) public onlyOwner returns (bool) {
        if(newAddr != bndesRegistryAddr) {

            if (bndesRegistryAddr != address(0)) {
                BNDESRegistry bndesRegistry = BNDESRegistry(bndesRegistryAddr);
                bndesRegistry.pause();
            }

            previousbndesRegistryAddr.push(bndesRegistryAddr);
            bndesRegistryAddr = newAddr;
            return true;
        }

        return false;
    }
    
    function getBNDESRegistryAddr() public view returns (address) {
        return bndesRegistryAddr;
    }
}
